import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline:
          "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-11 rounded-md px-8",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  }
)
Button.displayName = "Button"

export { Button, buttonVariants }

import * as React from "react"

import { cn } from "@/lib/utils"

export interface InputProps
  extends React.InputHTMLAttributes<HTMLInputElement> {}

const Input = React.forwardRef<HTMLInputElement, InputProps>(
  ({ className, type, ...props }, ref) => {
    return (
      <input
        type={type}
        className={cn(
          "flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50",
          className
        )}
        ref={ref}
        {...props}
      />
    )
  }
)
Input.displayName = "Input"

export { Input }

"use client";

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";
import { LockKeyhole } from "lucide-react";
import { cn } from "@/lib/utils";

const emailInputVariants = cva(
  "flex items-center gap-2 overflow-hidden rounded-[10px] bg-transparent px-3 py-3 shadow-[0px_4px_16px_rgba(0,0,0,0.15)] transition-all",
  {
    variants: {
      responsive: {
        desktop: "w-[470px]",
        mobile: "w-[336px]",
      },
    },
    defaultVariants: {
      responsive: "desktop",
    },
  }
);

export interface EmailSignupInputProps
  extends Omit<React.InputHTMLAttributes<HTMLInputElement>, "size">,
    VariantProps<typeof emailInputVariants> {
  onSubmit?: () => void;
}

const EmailSignupInput = React.forwardRef<
  HTMLInputElement,
  EmailSignupInputProps
>(({ className, responsive, onSubmit, ...props }, ref) => {
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit?.();
  };

  return (
    <form
      onSubmit={handleSubmit}
      className={cn(emailInputVariants({ responsive }), className)}
    >
      <input
        ref={ref}
        type="email"
        placeholder="Your Email"
        className={cn(
          "flex-1 bg-transparent text-[16px] text-[#FFF5DC] placeholder:text-[#FFF5DC] focus-visible:outline-none md:text-[16px]",
          "text-[14px]"
        )}
        {...props}
      />
      <button
        type="submit"
        className="flex h-8 w-8 items-center justify-center rounded-full bg-white/10 transition-colors hover:bg-white/20 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-white focus-visible:ring-offset-2 focus-visible:ring-offset-transparent"
        aria-label="Submit email"
      >
        <LockKeyhole className="h-4 w-4 text-white" strokeWidth={1.5} />
      </button>
    </form>
  );
});

EmailSignupInput.displayName = "EmailSignupInput";

export { EmailSignupInput, emailInputVariants };

